@push('script')
    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // Check if no users are found
            @if (auth()->user()->membership() && $users->isEmpty() && request()->filled('search'))
                Swal.fire({
                    imageUrl: '{{ asset('frontend/images/logo.svg') }}',
                    imageWidth: 100,
                    title: 'No Users Found',
                    text: 'Sorry, no users match your search criteria.',
                    confirmButtonText: 'OK'
                });
            @endif
        });
    </script>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            document.querySelectorAll('.view-profile-btn').forEach(function(button) {
                button.addEventListener('click', function(event) {
                    event.preventDefault();
                    var userId = this.getAttribute('data-user-id');
                    var url = '{{ route('trade.profile', ':id') }}'.replace(':id', userId);

                    fetch(url, {
                            method: 'GET',
                            headers: {
                                'X-Requested-With': 'XMLHttpRequest',
                                'X-CSRF-TOKEN': '{{ csrf_token() }}'
                            }
                        })
                        .then(response => response.json())
                        .then(data => {
                            if (data.status === 'error') {
                                Swal.fire({
                                    imageUrl: '{{ asset('frontend/images/logo.svg') }}',
                                    imageWidth: 100,
                                    title: data.title,
                                    text: data.message,
                                    showCancelButton: true,
                                    confirmButtonText: 'Purchased New Subscription',
                                    cancelButtonText: 'Cancel',
                                    showConfirmButton: true,
                                    input: null,
                                    preConfirm: () => {
                                        window.location.href =
                                            '{{ route('trade.frontend.subscription') }}';
                                    }
                                });
                            } else if (data.status === 'success') {
                                window.location.href = url;
                            }
                        });
                });
            });
        });
    </script>
@endpush
